  let(:invalid_session) { {user_id: nil} }
  
  let(:invalid_owner_session) {{user_id: User.third.id}}
  
  describe "GET #index" do
    context "i'm admin", :admin do
      it "assigns all litiges as @litiges" do
        litige = Litige.create! valid_attributes
        get :index, {}
        expect(assigns(:litiges)).to eq(Litige.all)
      end
    end
    
    context "i'm not admin", :owner do
      it "assigns all litiges as @litiges" do
        litige = Litige.create! valid_attributes
        get :index, {}
        expect(assigns(:litiges)).to eq(Litige.tri(@user.id))
