      it "assigns the requested litige as @litige" do
        litige = Litige.create! valid_attributes
        get :show, {:id => litige.to_param}
        expect(assigns(:litige)).to eq(litige)
      end
    end
    
    context "i'm owner", :owner do
      it "assigns the requested litige as @litige" do
        litige = Litige.create! valid_attributes
        get :show, {:id => litige.to_param}
        expect(assigns(:litige)).to eq(litige)
